INSERT INTO `his_config_info` VALUES (0, 1, 'youlai-auth.yaml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    driver-class-name: com.mysql.cj.jdbc.Driver \n    url: jdbc:mysql://${mysql.host}:${mysql.port}/oauth2_server?zeroDateTimeBehavior=convertToNull&useUnicode=true&characterEncoding=UTF-8&serverTimezone=Asia/Shanghai&autoReconnect=true\n    username: ${mysql.username}\n    password: ${mysql.password}\n  data:\n    redis:\n      database: ${redis.database}\n      host: ${redis.host}\n      port: ${redis.port}\n      password: ${redis.password}\n      lettuce:\n        pool:\n          min-idle: 1\n \nfeign:\n  httpclient:\n    enabled: true\n  okhttp:\n    enabled: false\n  sentinel:  # 开启feign对sentinel的支持\n    enabled: false \n\nmybatis-plus:\n  configuration:\n    # 驼峰下划线转换\n    map-underscore-to-camel-case: true\n    # 这个配置会将执行的sql打印出来，在开发或测试的时候可以用\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n    default-enum-type-handler: org.apache.ibatis.type.EnumOrdinalTypeHandler\n\n# 微信小程序\nwx:\n  miniapp:\n    appId: wx99a151dc43d2637b\n    appSecret: a09605af8ad29ca5d18ff31c19828f37\n\n# springdoc-openapi项目配置\nspringdoc:\n  default-flat-param-object: true\n  swagger-ui:\n    path: /swagger-ui.html\n    tags-sorter: alpha\n    operations-sorter: alpha\n  api-docs:\n    path: /v3/api-docs\n  group-configs:\n    - group: \'default\'\n      paths-to-match: \'/**\'\n      packages-to-scan: com.youlai.auth.controller\n  # Api文档信息\n  info:\n    title: 认证服务\n    version: 3.0.0\n    description: 认证、登出、验证码接口\n    contact:\n      name: youlai\n      url: https://www.youlai.tech\n      email: youlaitech@163.com\n    license:\n      name: Apache 2.0\n      url: https://www.apache.org/licenses/LICENSE-2.0.html\n\n# knife4j的增强配置，不需要增强可以不配\nknife4j:\n  enable: false\n  setting:\n    language: zh_cn\n\n# 验证码配置\ncaptcha:\n  # 验证码类型 circle-圆圈干扰验证码|gif-Gif验证码|line-干扰线验证码|shear-扭曲干扰验证码\n  type: circle\n  # 验证码宽度\n  width: 120\n  # 验证码高度\n  height: 40\n  # 验证码干扰元素个数\n  interfere-count: 2\n  # 文本透明度(0.0-1.0)\n  text-alpha: 0.8\n  # 验证码字符配置\n  code:\n    # 验证码字符类型 math-算术|random-随机字符\n    type: math\n    # 验证码字符长度，type=算术时，表示运算位数(1:个位数运算 2:十位数运算)；type=随机字符时，表示字符个数\n    length: 1\n  # 验证码字体\n  font:\n    # 字体名称 Dialog|DialogInput|Monospaced|Serif|SansSerif\n    name: SansSerif\n    # 字体样式 0-普通|1-粗体|2-斜体\n    weight: 1\n    # 字体大小\n    size: 24\n  # 验证码有效期(秒)\n  expire-seconds: 120\n\n# 安全配置\nsecurity:\n  # 白名单\n  ignore-uris:\n    - /api/v1/auth/captcha\n    - /api/v1/auth/sms_code', '5cdf5c362ded2fb5733ed7379345980f', '2010-05-05 00:00:00', '2024-11-10 09:05:15', NULL, '172.27.0.1', 'I', '', '');
INSERT INTO `his_config_info` VALUES (0, 2, 'youlai-common.yaml', 'DEFAULT_GROUP', '', 'redis:\n  database: 9\n  host: www.youlai.tech\n  port: 6379\n  password: 123456\n  \nmysql:\n  host: localhost\n  port: 3306\n  username: root\n  password: 123456\n\nrabbitmq:\n  host: www.youlai.tech\n  port: 5672\n  username: guest\n  password: guest\n\n# 网关地址\ngateway:\n  endpoint: http://localhost:9999\n\n# Nacos 配置中心地址\nnacos:\n  server-addr: http://localhost:8848\n\n# 验签公钥地址\nspring:\n  security:\n    oauth2:\n      authorizationserver:\n        token-uri: ${gateway.endpoint}/youlai-auth/oauth2/token\n      resourceserver:\n        jwt: \n          jwk-set-uri: ${gateway.endpoint}/youlai-auth/oauth2/jwks\n\n# 短信配置\nsms:\n  # 阿里云短信\n  aliyun:\n    accessKeyId: LTAI5tSMgfxxxxxxdiBJLyR\n    accessKeySecret: SoOWRqpjtS7xxxxxxZ2PZiMTJOVC\n    domain: dysmsapi.aliyuncs.com \n    regionId: cn-shanghai\n    signName: 有来技术\n    templateCodes: \n      register: SMS_22xxx771\n      login: SMS_22xxx770\n\n\n', 'c2c11e462e99b3aa7eb34568eef53766', '2010-05-05 00:00:00', '2024-11-10 09:05:15', NULL, '172.27.0.1', 'I', '', '');
INSERT INTO `his_config_info` VALUES (0, 3, 'youlai-gateway.yaml', 'DEFAULT_GROUP', '', 'spring:\n  data:\n    redis:\n      timeout: PT30S\n      database: ${redis.database}\n      host: ${redis.host}\n      port: ${redis.port}\n      password: ${redis.password}\n      lettuce:\n        pool:\n          # 连接池最小空闲连接数\n          min-idle: 1\n  cloud:\n    gateway:\n      discovery:\n        locator:\n          enabled: true # 启用服务发现\n          lower-case-service-id: true\n      default-filters:\n        # 令牌中继\n        - TokenRelay= \n      routes:\n        - id: 认证中心\n          uri: lb://youlai-auth\n          predicates:\n            - Path=/youlai-auth/**\n          filters:\n            - StripPrefix=1            \n        - id: 系统服务\n          uri: lb://youlai-system\n          predicates:\n            - Path=/youlai-system/**\n          filters:\n            - StripPrefix=1            \n      globalcors:\n        corsConfigurations:\n          \'[/**]\': # 对所有路由应用CORS配置\n            # 允许所有域名的请求\n            allowedOriginPatterns: \"*\"\n            # 允许所有请求方法，如GET、POST、PUT、DELETE等\n            allowedMethods: \"*\"\n             # 允许所有请求头\n            allowedHeaders: \"*\" \n            # 允许凭证，如Cookies和HTTP认证\n            allowCredentials: true   \n            maxAge: 3600\n# Feign 配置\nfeign:\n  httpclient:\n    enabled: true\n  okhttp:\n    enabled: false\n  sentinel:  # 开启feign对sentinel的支持\n    enabled: false \n\n\n# 安全配置\nsecurity:\n  # 访问黑名单\n  blacklist-paths:\n      # 获取用户认证信息\n      - /youlai-system/api/v1/users/{username}/authInfo\n\n# knife4j 网关聚合  \n# https://doc.xiaominfo.com/docs/middleware-sources/spring-cloud-gateway/spring-gateway-introduction\nknife4j:\n  gateway:\n    enabled: true\n    # 指定服务发现的模式聚合微服务文档，并且是默认`default`分组\n    strategy: discover\n    discover:\n      enabled: true\n      # 聚合所有子服务(swagger2规范)，子服务是3规范则替换为openapi3\n      version: openapi3\n      # 需要排除的微服务(eg:网关服务) \n      excluded-services:\n          - youlai-gateway\n\n', 'e6cce3c03f75badce3eef101cd14a302', '2010-05-05 00:00:00', '2024-11-10 09:05:15', NULL, '172.27.0.1', 'I', '', '');
INSERT INTO `his_config_info` VALUES (0, 4, 'youlai-system.yaml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    driver-class-name: com.mysql.cj.jdbc.Driver \n    url: jdbc:mysql://${mysql.host}:${mysql.port}/youlai_system?zeroDateTimeBehavior=convertToNull&useUnicode=true&characterEncoding=UTF-8&serverTimezone=Asia/Shanghai&autoReconnect=true\n    username: ${mysql.username}\n    password: ${mysql.password}\n  data:\n    redis:\n      database: ${redis.database}\n      host: ${redis.host}\n      port: ${redis.port}\n      password: ${redis.password}\n      lettuce:\n        pool:\n          min-idle: 1\n  cache:\n    # 缓存类型 redis、none(不使用缓存) \n    type: redis\n    # 缓存时间(单位：ms)\n    redis:\n      time-to-live: 3600000\n      # 缓存null值，防止缓存穿透\n      cache-null-values: true\n      \n  # RabbitMQ 配置\n  rabbitmq:\n    host: ${rabbitmq.host}\n    port: ${rabbitmq.port}\n    username: ${rabbitmq.username}\n    password: ${rabbitmq.password}\n    virtual-host: /\n    listener:\n      simple:\n        acknowledge-mode: manual   \n\n  cloud:\n    sentinel:\n      enabled: false\n      eager: true # 取消控制台懒加载，项目启动即连接Sentinel\n      transport:\n        client-ip: localhost\n        dashboard: localhost:8080\n      datasource:\n        # 限流规则，flow为key，随便定义\n        flow:\n          nacos:\n            server-addr: ${spring.cloud.nacos.discovery.server-addr}\n            dataId: ${spring.application.name}-flow-rules\n            groupId: SENTINEL_GROUP\n            data-type: json\n            rule-type: flow\n        # 降级规则\n        degrade:\n          nacos:\n            server-addr: ${spring.cloud.nacos.discovery.server-addr}\n            dataId: ${spring.application.name}-degrade-rules\n            groupId: SENTINEL_GROUP\n            data-type: json\n            rule-type: degrade\n                \nmybatis-plus:\n  configuration:\n    # 驼峰下划线转换\n    map-underscore-to-camel-case: true\n    # 这个配置会将执行的sql打印出来，在开发或测试的时候可以用\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n    default-enum-type-handler: org.apache.ibatis.type.EnumOrdinalTypeHandler\n\nfeign:\n  httpclient:\n    enabled: true\n  okhttp:\n    enabled: false\n  sentinel:  # 开启feign对sentinel的支持\n    enabled: false \n\n# 安全配置\nsecurity:\n  # 白名单路径列表\n  ignore-uris:\n    - /api/v1/users/{username}/authInfo\n    - /api/v1/users/test-whitelist\n\n# springdoc-openapi项目配置\nspringdoc:\n  default-flat-param-object: true\n  swagger-ui:\n    path: /swagger-ui.html\n    tags-sorter: alpha\n    operations-sorter: alpha\n  api-docs:\n    path: /v3/api-docs\n  group-configs:\n    - group: \'default\'\n      paths-to-match: \'/**\'\n      packages-to-scan: \n        - com.youlai.system.controller\n        - com.youlai.common.file.controller\n  # Api文档信息\n  info:\n    title: 系统服务\n    version: 3.0.0\n    description: 用户、部门、角色、菜单、字典等接口\n    contact:\n      name: youlai\n      url: https://www.youlai.tech\n      email: youlaitech@163.com\n    license:\n      name: Apache 2.0\n      url: https://www.apache.org/licenses/LICENSE-2.0.html\n\n# knife4j的增强配置，不需要增强可以不配\nknife4j:\n  enable: false\n  setting:\n    language: zh_cn\n\n# 文件存储\noss:\n  # OSS 类型 (目前支持aliyun、minio)\n  type: aliyun\n  # MinIO 对象存储服务\n  minio:\n    # 服务Endpoint\n    endpoint: http://localhost:9000\n    # 访问凭据\n    access-key: minioadmin\n    # 凭据密钥\n    secret-key: minioadmin\n    # 存储桶名称\n    bucket-name: default\n    # (可选)自定义域名，如果配置了域名，生成的文件URL是域名格式，未配置则URL则是IP格式 (eg: https://oss.youlai.tech)\n    custom-domain:\n  # 阿里云OSS对象存储服务\n  aliyun:\n    # 服务Endpoint\n    endpoint: oss-cn-hangzhou.aliyuncs.com\n    # 访问凭据\n    access-key-id: your-access-key-id\n    # 凭据密钥\n    access-key-secret: your-access-key-secret\n    # 存储桶名称\n    bucket-name: default\n', '659f1507c043e602b132619d788bdf21', '2010-05-05 00:00:00', '2024-11-10 09:05:15', NULL, '172.27.0.1', 'I', '', '');
INSERT INTO `his_config_info` VALUES (0, 5, 'seataServer.properties', 'DEFAULT_GROUP', '', '#For details about configuration items, see https://seata.io/zh-cn/docs/user/configurations.html\n#Transport configuration, for client and server\ntransport.type=TCP\ntransport.server=NIO\ntransport.heartbeat=true\ntransport.enableTmClientBatchSendRequest=false\ntransport.enableRmClientBatchSendRequest=true\ntransport.enableTcServerBatchSendResponse=false\ntransport.rpcRmRequestTimeout=30000\ntransport.rpcTmRequestTimeout=30000\ntransport.rpcTcRequestTimeout=30000\ntransport.threadFactory.bossThreadPrefix=NettyBoss\ntransport.threadFactory.workerThreadPrefix=NettyServerNIOWorker\ntransport.threadFactory.serverExecutorThreadPrefix=NettyServerBizHandler\ntransport.threadFactory.shareBossWorker=false\ntransport.threadFactory.clientSelectorThreadPrefix=NettyClientSelector\ntransport.threadFactory.clientSelectorThreadSize=1\ntransport.threadFactory.clientWorkerThreadPrefix=NettyClientWorkerThread\ntransport.threadFactory.bossThreadSize=1\ntransport.threadFactory.workerThreadSize=default\ntransport.shutdown.wait=3\ntransport.serialization=seata\ntransport.compressor=none\n\n#Transaction routing rules configuration, only for the client\nservice.vgroupMapping.default_tx_group=default\n#If you use a registry, you can ignore it\nservice.default.grouplist=127.0.0.1:8091\nservice.enableDegrade=false\nservice.disableGlobalTransaction=false\n\n#Transaction rule configuration, only for the client\nclient.rm.asyncCommitBufferLimit=10000\nclient.rm.lock.retryInterval=10\nclient.rm.lock.retryTimes=30\nclient.rm.lock.retryPolicyBranchRollbackOnConflict=true\nclient.rm.reportRetryCount=5\nclient.rm.tableMetaCheckEnable=true\nclient.rm.tableMetaCheckerInterval=60000\nclient.rm.sqlParserType=druid\nclient.rm.reportSuccessEnable=false\nclient.rm.sagaBranchRegisterEnable=false\nclient.rm.sagaJsonParser=fastjson\nclient.rm.tccActionInterceptorOrder=-2147482648\nclient.tm.commitRetryCount=5\nclient.tm