: ${spring.application.name}-degrade-rules\n            groupId: SENTINEL_GROUP\n            data-type: json\n            rule-type: degrade\n                \nmybatis-plus:\n  configuration:\n    # 驼峰下划线转换\n    map-underscore-to-camel-case: true\n    # 这个配置会将执行的sql打印出来，在开发或测试的时候可以用\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n    default-enum-type-handler: org.apache.ibatis.type.EnumOrdinalTypeHandler\n\nfeign:\n  httpclient:\n    enabled: true\n  okhttp:\n    enabled: false\n  sentinel:  # 开启feign对sentinel的支持\n    enabled: false \n\n# 安全配置\nsecurity:\n  # 白名单路径列表\n  ignore-uris:\n    - /api/v1/users/{username}/authInfo\n    - /api/v1/users/test-whitelist\n\n# springdoc-openapi项目配置\nspringdoc:\n  default-flat-param-object: true\n  swagger-ui:\n    path: /swagger-ui.html\n    tags-sorter: alpha\n    operations-sorter: alpha\n  api-docs:\n    path: /v3/api-docs\n  group-configs:\n    - group: \'default\'\n      paths-to-match: \'/**\'\n      packages-to-scan: \n        - com.youlai.system.controller\n        - com.youlai.common.file.controller\n  # Api文档信息\n  info:\n    title: 系统服务\n    version: 3.0.0\n    description: 用户、部门、角色、菜单、字典等接口\n    contact:\n      name: youlai\n      url: https://www.youlai.tech\n      email: youlaitech@163.com\n    license:\n      name: Apache 2.0\n      url: https://www.apache.org/licenses/LICENSE-2.0.html\n\n# knife4j的增强配置，不需要增强可以不配\nknife4j:\n  enable: false\n  setting:\n    language: zh_cn\n\n# 文件存储\noss:\n  # OSS 类型 (目前支持aliyun、minio)\n  type: aliyun\n  # MinIO 对象存储服务\n  minio:\n    # 服务Endpoint\n    endpoint: http://localhost:9000\n    # 访问凭据\n    access-key: minioadmin\n    # 凭据密钥\n    secret-key: minioadmin\n    # 存储桶名称\n    bucket-name: default\n    # (可选)自定义域名，如果配置了域名，生成的文件URL是域名格式，未配置则URL则是IP格式 (eg: https://oss.youlai.tech)\n    custom-domain:\n  # 阿里云OSS对象存储服务\n  aliyun:\n    # 服务Endpoint\n    endpoint: oss-cn-hangzhou.aliyuncs.com\n    # 访问凭据\n    access-key-id: your-access-key-id\n    # 凭据密钥\n    access-key-secret: your-access-key-secret\n    # 存储桶名称\n    bucket-name: default\n', '1c350835d7582207d47d6248a9e93c48', '2010-05-05 00:00:00', '2024-11-10 16:37:18', NULL, '172.30.0.1', 'U', '', '');
INSERT INTO `his_config_info` VALUES (6, 21, 'mall-product.yaml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    driver-class-name: com.mysql.cj.jdbc.Driver\n    url: jdbc:mysql://${mysql.host}:${mysql.port}/youlai_mall_pms?zeroDateTimeBehavior=convertToNull&useUnicode=true&characterEncoding=UTF-8&serverTimezone=Asia/Shanghai&autoReconnect=true\n    username: ${mysql.username}\n    password: ${mysql.password}\n  data:\n    redis:\n      database: ${redis.database}\n      host: ${redis.host}\n      port: ${redis.port}\n      password: ${redis.password}\n      lettuce:\n        pool:\n          min-idle: 1\n  # RabbitMQ 配置\n  rabbitmq:\n    host: ${rabbitmq.host}\n    port: ${rabbitmq.port}\n    username: ${rabbitmq.username}\n    password: ${rabbitmq.password}\n    virtual-host: /        \n  cache:\n    # 缓存类型\n    type: redis\n    # 缓存时间(单位：ms)\n    redis:\n      time-to-live: 3600000\n      # 缓存null值，防止缓存穿透\n      cache-null-values: true\n      # 允许使用缓存前缀，\n      use-key-prefix: true\n      # 缓存前缀，没有设置使用注解的缓存名称(value)作为前缀，和注解的key用双冒号::拼接组成完整缓存key\n      # key-prefix:\n\nmybatis-plus:\n  configuration:\n    # 驼峰下划线转换 \n    map-underscore-to-camel-case: true\n    # 这个配置会将执行的sql打印出来，在开发或测试的时候可以用\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n\n# Seata 配置\nseata: \n  # 开启 Seata\n  enabled: true\n  # 事务分组名称\n  tx-service-group: default_tx_group\n  service:\n    # 事务分组映射，根据分组名称匹配到集群\n    vgroup-mapping:\n      # 事务分组名称：集群名称\n      default_tx_group: default\n  registry:\n    type: nacos\n    nacos:\n      application: seata-server\n      server-addr: ${nacos.server-addr}\n      namespace:\n      group: SEATA_GROUP \n      \n# feign 配置\nfeign:\n  httpclient:\n    enabled: true\n  okhttp:\n    enabled: false\n  sentinel:  # 开启feign对sentinel的支持\n    enabled: false \n\n# 安全配置\nsecurity:\n  # 白名单\n  ignore-uris:\n      - /app-api/v1/categories\n      - /app-api/v1/spu/**\n\n# springdoc-openapi项目配置\nspringdoc:\n  # 是否缺省注解 @ParameterObject\n  default-flat-param-object: true\n  swagger-ui:\n    path: /swagger-ui.html\n    tags-sorter: alpha\n    operations-sorter: order\n  api-docs:\n    path: /v3/api-docs\n  group-configs:\n    - group: \'default\'\n      paths-to-match: \'/**\'\n      packages-to-scan: com.youlai.mall.product.controller\n  # Api文档信息\n  info:\n    title: 商品服务\n    version: 3.0.0\n    description: 库存、分类、品牌、规格、参数等接口\n    contact:\n      name: youlai\n      url: https://www.youlai.tech\n      email: youlaitech@163.com\n    license:\n      name: Apache 2.0\n      url: https://www.apache.org/licenses/LICENSE-2.0.html\n\n# knife4j的增强配置，不需要增强可以不配\nknife4j:\n  enable: true\n  setting:\n    language: zh_cn', '138059c4f2e8f30162aa3cf8a31e3821', '2010-05-05 00:00:00', '2024-11-10 16:37:35', NULL, '172.30.0.1', 'U', '', '');
