INSERT INTO `his_config_info` VALUES (1, 23, 'youlai-auth.yaml', 'DEFAULT_GROUP', '', 'spring:\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    driver-class-name: com.mysql.cj.jdbc.Driver \n    url: jdbc:mysql://${mysql.host}:${mysql.port}/youlai_oauth2_server?zeroDateTimeBehavior=convertToNull&useUnicode=true&characterEncoding=UTF-8&serverTimezone=Asia/Shanghai&autoReconnect=true\n    username: ${mysql.username}\n    password: ${mysql.password}\n  data:\n    redis:\n      database: ${redis.database}\n      host: ${redis.host}\n      port: ${redis.port}\n      password: ${redis.password}\n      lettuce:\n        pool:\n          min-idle: 1\n \nfeign:\n  httpclient:\n    enabled: true\n  okhttp:\n    enabled: false\n  sentinel:  # 开启feign对sentinel的支持\n    enabled: false \n\nmybatis-plus:\n  configuration:\n    # 驼峰下划线转换\n    map-underscore-to-camel-case: true\n    # 这个配置会将执行的sql打印出来，在开发或测试的时候可以用\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n    default-enum-type-handler: org.apache.ibatis.type.EnumOrdinalTypeHandler\n\n# 微信小程序\nwx:\n  miniapp:\n    appId: wx99a151dc43d2637b\n    appSecret: a09605af8ad29ca5d18ff31c19828f37\n\n# springdoc-openapi项目配置\nspringdoc:\n  default-flat-param-object: true\n  swagger-ui:\n    path: /swagger-ui.html\n    tags-sorter: alpha\n    operations-sorter: alpha\n  api-docs:\n    path: /v3/api-docs\n  group-configs:\n    - group: \'default\'\n      paths-to-match: \'/**\'\n      packages-to-scan: com.youlai.auth.controller\n  # Api文档信息\n  info:\n    title: 认证服务\n    version: 3.0.0\n    description: 认证、登出、验证码接口\n    contact:\n      name: youlai\n      url: https://www.youlai.tech\n      email: youlaitech@163.com\n    license:\n      name: Apache 2.0\n      url: https://www.apache.org/licenses/LICENSE-2.0.html\n\n# knife4j的增强配置，不需要增强可以不配\nknife4j:\n  enable: false\n  setting:\n    language: zh_cn\n\n# 验证码配置\ncaptcha:\n  # 验证码类型 circle-圆圈干扰验证码|gif-Gif验证码|line-干扰线验证码|shear-扭曲干扰验证码\n  type: circle\n  # 验证码宽度\n  width: 120\n  # 验证码高度\n  height: 40\n  # 验证码干扰元素个数\n  interfere-count: 2\n  # 文本透明度(0.0-1.0)\n  text-alpha: 0.8\n  # 验证码字符配置\n  code:\n    # 验证码字符类型 math-算术|random-随机字符\n    type: math\n    # 验证码字符长度，type=算术时，表示运算位数(1:个位数运算 2:十位数运算)；type=随机字符时，表示字符个数\n    length: 1\n  # 验证码字体\n  font:\n    # 字体名称 Dialog|DialogInput|Monospaced|Serif|SansSerif\n    name: SansSerif\n    # 字体样式 0-普通|1-粗体|2-斜体\n    weight: 1\n    # 字体大小\n    size: 24\n  # 验证码有效期(秒)\n  expire-seconds: 120\n\n# 安全配置\nsecurity:\n  # 白名单\n  ignore-uris:\n    - /api/v1/auth/captcha\n    - /api/v1/auth/sms_code', 'f34c2abdb671b4db88ea135e27012b26', '2010-05-05 00:00:00', '2024-11-10 16:38:03', NULL, '172.30.0.1', 'U', '', '');
INSERT INTO `his_config_info` VALUES (7, 24, 'mall-order.yaml', 'DEFAULT_GROUP', '', 'spring:\n  datasource: \n    type: com.alibaba.druid.pool.DruidDataSource\n    driver-class-name: com.mysql.cj.jdbc.Driver\n    url: jdbc:mysql://${mysql.host}:${mysql.port}/youlai_mall_oms?zeroDateTimeBehavior=convertToNull&useUnicode=true&characterEncoding=UTF-8&serverTimezone=Asia/Shanghai&autoReconnect=true\n    username: ${mysql.username}\n    password: ${mysql.password}\n  # RabbitMQ 配置\n  rabbitmq:\n    host: ${rabbitmq.host}\n    port: ${rabbitmq.port}\n    username: ${rabbitmq.username}\n    password: ${rabbitmq.password}\n    virtual-host: /\n    listener:\n      simple:\n        acknowledge-mode: manual   \n  data:\n    redis:\n      database: ${redis.database}\n      host: ${redis.host}\n      port: ${redis.port}\n      password: ${redis.password}\n      lettuce:\n        pool:\n          min-idle: 1\nmybatis-plus:\n  configuration:\n    # 驼峰下划线转换\n    map-underscore-to-camel-case: true\n    # 这个配置会将执行的sql打印出来，在开发或测试的时候可以用\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n\n# Seata 配置\nseata: \n  # 开启 Seata\n  enabled: true\n  # 事务分组名称\n  tx-service-group: default_tx_group\n  service:\n    # 事务分组映射，根据分组名称匹配到集群\n    vgroup-mapping:\n      # 事务分组名称：集群名称\n      default_tx_group: default\n  registry:\n    type: nacos\n    nacos:\n      application: seata-server\n      server-addr: ${nacos.server-addr}\n      namespace:\n      group: SEATA_GROUP  \n\n# Feign 配置\nfeign:\n  httpclient:\n    enabled: true\n  okhttp:\n    enabled: false\n  sentinel:  # 开启feign对sentinel的支持\n    enabled: false\n\n# 安全配置\nsecurity:\n  # 白名单\n  ignore-uris:\n    - /app-api/v1/carts\n\n# springdoc-openapi项目配置\nspringdoc:\n  # 是否缺省注解 @ParameterObject\n  default-flat-param-object: true\n  swagger-ui:\n    path: /swagger-ui.html\n    tags-sorter: alpha\n    operations-sorter: alpha\n  api-docs:\n    path: /v3/api-docs\n  group-configs:\n    - group: \'default\'\n      paths-to-match: \'/**\'\n      packages-to-scan: com.youlai.mall.order.controller\n  # Api文档信息\n  info:\n    title: 订单服务\n    version: 3.0.0\n    description: 订单、购物车等接口\n    contact:\n      name: youlai\n      url: https://www.youlai.tech\n      email: youlaitech@163.com\n    license:\n      name: Apache 2.0\n      url: https://www.apache.org/licenses/LICENSE-2.0.html\n\n# Knife4j 接口文档配置\nknife4j:\n  # 是否开启增强配置\n  enable: true\n  # 是否为生产环境，如果设置为 true，表示当前是生产环境，将会关闭接口文档的访问，在生产环境中关闭接口文档可以提高安全性\n  production: false\n  setting:\n    language: zh_cn\n\n# 微信支付配置 https://github.com/binarywang/weixin-java-pay-demo/blob/master/src/main/resources/application.yml.template\nwx:\n  pay:\n    # 微信公众号或者小程序等的appid\n    appId:\n    # 微信支付商户号\n    mchId: 12345678\n    # 微信支付商户密钥 \n    mchKey: abcdefghijklmn\n    # 商户私钥文件\n    privateKeyPath: classpath:apiclient_key.pem\n    # 商户公钥文件\n    privateCertPath: classpath:apiclient_cert.pem\n    # APIv3密钥\n    apiV3Key: xxxxxxxxxxxxxxxxxxxxx\n    # 微信支付异步回掉地址，通知url必须为直接可访问的url，不能携带参数\n    notifyUrl: ${gateway.endpoint}/mall-order/common-api/v3/wx-pay/notify/order\n    # 退款回调地址\n    refundNotifyUrl: ${gateway.endpoint}/mall-order/common-api/v3/wx-pay/notify/refund', 'f4e60dd003612a378772c8fe3c040cbb', '2010-05-05 00:00:00', '2024-11-10 16:38:33', NULL, '172.30.0.1', 'U', '', '');
