mapper.select:
SELECT u.id, u.username, u.nickname, u.mobile, u.gender, u.avatar, u.STATUS, u.email, d.NAME AS dept_name, GROUP_CONCAT( r.NAME ) AS roleNames, u.create_time FROM sys_user u LEFT JOIN sys_dept d ON u.dept_id = d.id LEFT JOIN sys_user_role sur ON u.id = sur.user_id LEFT JOIN sys_role r ON sur.role_id = r.id
u.is_deleted = 0
AND NOT EXISTS ( SELECT 1 FROM sys_user_role sur INNER JOIN sys_role r ON sur.role_id = r.id WHERE sur.user_id = u.id AND r.code = '${@com.youlai.common.constant.SystemConstants@ROOT_ROLE_CODE}' )
AND ( u.username LIKE CONCAT('%',#{queryParams.keywords},'%') OR u.nickname LIKE CONCAT('%',#{queryParams.keywords},'%') OR u.mobile LIKE CONCAT('%',#{queryParams.keywords},'%') )
AND u.status = #{queryParams.status}
AND concat(',',concat(d.tree_path,',',d.id),',') like concat('%,',#{queryParams.deptId},',%')
AND u.create_time >= #{startDate}
AND u.create_time <= #{endDate}
AND sur.role_id IN
#{roleId}
GROUP BY u.id
ORDER BY u.${queryParams.field} ${queryParams.direction}
ORDER BY u.update_time DESC, u.create_time DESC
